    private async Task ChangeThemeAsync()
    {
        if (_themeName == "light")
        {
            _currentTheme = _darkTheme;
            _themeName = "dark";
        }
        else
        {
            _currentTheme = _lightTheme;
            _themeName = "light";
        }

        await LocalStorage.SetItemAsStringAsync("theme", _themeName);
    }


